    private void updateDisplay(String value) {
        String currentText = field.getText();
        if (currentText.equals("0") || !isTyping) {
            field.setText(value);
            isTyping = true;
        } else {
            field.setText(currentText + value);
        }
    }
    
    private void digitButtonActionPerformed(JButton button) {
        String buttonText = button.getText();
        updateDisplay(buttonText);
    }
    
        //handle operator buttons
    private void operatorButtonActionPerformed(char op) {
        if (isTyping) {
            operand1 = Double.parseDouble(field.getText());
            operator = op;
            isTyping = false;
        }
    }
    
