    //handle clear button
    private void clearButtonActionPerformed() {
        operand1 = 0;
        operand2 = 0;
        operator = ' ';
        field.setText("0");
        isTyping = false;
    }
    
    private void eraseBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eraseBTNActionPerformed
        // TODO add your handling code here:
        if (isTyping && field.getText().length() > 0) {
            String currentText = field.getText();
            field.setText(currentText.substring(0, currentText.length() - 1));
            if (field.getText().isEmpty()) {
                field.setText("0");
                isTyping = false;
            }
        }
    }//GEN-LAST:event_eraseBTNActionPerformed

