    //handle equals button
    private void equalsButtonActionPerformed() {
        if (!isTyping) {
            return;
        }
        operand2 = Double.parseDouble(field.getText());
        double result = 0;
        switch (operator) {
            case '+':
                result = operand1 + operand2;
                break;
            case '-':
                result = operand1 - operand2;
                break;
            case '*':
                result = operand1 * operand2;
                break;
            case '/':
                if (operand2 != 0) {
                    result = operand1 / operand2;
                } else {
                    result = Double.POSITIVE_INFINITY; // Handle division by zero
                }
                break;
            case '%':
                result = operand1 % operand2;
                break;
        }
        field.setText(String.valueOf(result));
        isTyping = false;
    }
    
